parameters.body/messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "TextBlock",
            "size": "Large",
            "weight": "Bolder",
            "text": "New Scenario Summited for Review"
        },
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "Image",
                            "style": "Person",
                            "size": "Small",
                            "url": "https://www.pikpng.com/pngl/m/258-2582087_png-transparent-daily-mindfulness-matrix-mind-atlas-think.png"
                        }
                    ],
                    "width": "auto"
                },
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "weight": "Bolder",
                            "text": "@{outputs('Get_Sponsor_Name')?['body/fullname']}",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "spacing": "None",
                            "text": "Created on: @{triggerOutputs()?['body/createdon']}",
                            "isSubtle": true,
                            "wrap": true
                        }
                    ],
                    "width": "stretch"
                }
            ]
        },
        {
            "type": "TextBlock",
            "wrap": true,
            "text": "@{triggerOutputs()?['body/tlc_name']} has been submitted and is ready for review by the Cross Cloud Solution Review Board in order to to align with the appropriate offering. Click the button below to see the details of the submission. "
        },
        {
            "type": "FactSet",
            "facts": [
                {
                    "title": "Customer & TPID",
                    "value": "@{triggerOutputs()?['body/tlc_customertext']}"
                }
            ]
        }
    ],
    "actions": [
        {
            "type": "Action.OpenUrl",
            "title": "More Details",
            "url": "https://teams.microsoft.com/l/channel/19%3AVZvuSWaqpI8YfBoCJ9wg5dMDhdIHaybmn6BgS7dzaZ41%40thread.tacv2/tab%3A%3Af34317c6-56db-4bca-bf86-6350b63d1085?groupId=fc20461a-f388-44b7-8291-e4c711631a48&tenantId=72f988bf-86f1-41af-91ab-2d7cd011db47&allowXTenantAccess=false",
            "style": "positive"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}
